import { Component, OnInit } from '@angular/core';
import { CookingService } from './cooking.service';
import { ICooking } from './ICooking';

@Component({
  selector: 'app-cooking-card',
  templateUrl: './cooking-card.component.html',
  styleUrls: ['./cooking-card.component.css']
})
export class CookingCardComponent implements OnInit {

  cookings: ICooking[] = [];

 constructor (private CookingService: CookingService) {}


  ngOnInit() : void {
    this.getCookings();
  }

  getCookings(): void {
    this.CookingService.getAllCookings().subscribe(cooks => this.cookings = cooks);
  }

}
