import { Component } from '@angular/core';
import { ICooking } from './ICooking';
import { CookingService } from './cooking.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Enjoying-Cooking';
  cookings: ICooking[] = [];

constructor (private CookingService: CookingService) {}


  ngOnInit() : void {
    this.getCookings();
  }

  getCookings(): void{
    this.CookingService.getAllCookings().subscribe(cookings => this.cookings = cookings);
  }
}
<h1>{{title}}</h1>
<app-nav></app-nav>
<main>
  <app-main-filter></app-main-filter>
  <app-cooking-card></app-cooking-card>
</main>
<app-footer></app-footer>
