import { Component, OnInit } from '@angular/core';
import { CookingsComponent } from '../cookings/cookings.component';
import { ICooking } from '../ICooking';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent implements OnInit {

  cooking: ICooking = {
    id: "Id",
    title: "Title",
    description: "Description",
    difficulty: "Difficulty",
    nearestCost: 0,
    price: 0,
  }

  constructor (private cookingComponent: CookingsComponent) {}

  getCooking(): void {
    this.cooking = this.cookingComponent.clickedCooking!;
  }

  ngOnInit() : void {
    this.getCooking();
  }
}
<div>
  <h3>{{cooking.id}}. {{cooking.title}}</h3>
  <img>
  <p>Description: {{cooking.description}}</p>
  <p>Difficulty: {{cooking.difficulty}}</p>
  <p>Nearest cost of products: {{cooking.nearestCost}}</p>
  <p>Price: {{cooking.price}}</p>
</div>
