import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ICooking } from './ICooking';
import { IUser } from './IUser';

@Injectable({
  providedIn: 'root'
})
export class CookingService {
 cookingsUrl: string = "https://localhost:32770/";

 httpOptions = {
  headers: new HttpHeaders({'Content-Type': 'application/json'})
 };

constructor(private http: HttpClient) {
 }

 getAllCookings(): Observable<ICooking[]>{
  return this.http.get<ICooking[]>(this.cookingsUrl + "/getcooks");
 }

 userSignIn(user: IUser){
  this.http.post<IUser>(this.cookingsUrl + "signin", user, this.httpOptions);
 }
 userSignUp(user: IUser){
  this.http.post<IUser>(this.cookingsUrl + "signup", user, this.httpOptions);
 }

}
