import { Component, OnInit } from '@angular/core';
import { CookingService } from '../cooking.service';

@Component({
  selector: 'app-authorization',
  templateUrl: './authorization.component.html',
  styleUrls: ['./authorization.component.css']
})
export class AuthorizationComponent implements OnInit {

  token: string = "";

  public isRegirtrated : boolean = false;

  userName: string = "u";
  login: string = "l";
  password: string = "p";

  constructor (private cookingService: CookingService) {}

  changeState(){
    this.isRegirtrated = !this.isRegirtrated;
  }



  signIn(): void {
    this.token = "fake signIn";
    this.cookingService.userSignIn(this.login, this.password).subscribe();
  }
  signUp(): void {
    this.token = "fake signUp";
    this.cookingService.userSignUp(this.userName, this.login, this.password).subscribe(
      {
        next:(data:any) => {this.token = data}
      }
    );
  }

  ngOnInit() : void {

  }

}
